import React, { useRef } from 'react';

export default function ToolBar(editorContent) {
    const log = () => {
        if (editorContent.current) {
          console.log(editorContent.current.getContent());
        }
    };

    // console.log(editorContent.curremt.getContent());
    return (
        <div className='toolBar'>
            <button onClick={log}>Save</button>
        </div>
    );
}
