import React, { useRef } from 'react';

export default function ToolBar(editorRef) {
    const log = () => {
        if (editorRef.current) {
          console.log(editorRef.current.getContent());
        }
      };
    return (
        <div className='toolBar'>
            <button onClick={log}>Save</button>
        </div>
    );
}
