import React, { useRef } from 'react';

export default function ToolBar(editorContent) {
    const content = editorContent(null);

    const log = () => {
        if (content) {
          console.log(content);
        }
    };

    // console.log(content.curremt.getContent());
    return (
        <div className='toolBar'>
            <button onClick={log}>Save</button>
        </div>
    );
}
