import { Editor } from '@tinymce/tinymce-react';
import React, { useRef } from 'react';
import config from '../config/config.json';
// import ToolBar from './ToolBar';
import { useState } from 'react';


export default function TextEditor() {
    const editorRef = useRef(null);
    const log = () => {
      if (editorRef.current) {
        const title = document.getElementById('documentTitle').value;
        const content = editorRef.current.getContent();
        const request = {
          method: 'POST',
          headers: {'Content-Type': 'application/json'},
          body: JSON.stringify({ title: title, content: content })
        };


        fetch(`http://localhost:1337/documents`, request)
          .then(response => response.json())
      }
    };


    return (
      <div className="editorContainer">
        <h1 className='appTitle'>React Text Editor</h1>
        <div className='toolBar'>
          <button className='saveButton' onClick={log}>Save</button>
        </div>

      <form className='documentTitle'>
        <label for="documentTitle">Document Title</label>
        <input type="text" id="documentTitle" className='titleField'></input>
      </form>

        <div className='textEditor'>
          <Editor
            apiKey={config.api_key}
            onInit={(evt, editor) => editorRef.current = editor}
            initialValue="<p>This is the initial content of the editor.</p>"
            init={{
              height: 500,
              menubar: false,
              plugins: [
                'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
                'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
                'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'
              ],
              toolbar: 'undo redo | blocks | ' +
                'bold italic forecolor | alignleft aligncenter ' +
                'alignright alignjustify | bullist numlist outdent indent | ' +
                'removeformat | help',
              content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'
            }}
          />
        </div>

      </div>
    );
  }
